// Helper function to generate a Roman-style date
func romanDate() string {
	months := []string{
		"Ianuarius", "Februarius", "Martius", "Aprilis", "Maius", "Iunius",
		"Iulius", "Augustus", "September", "October", "November", "December",
	}

	now := time.Now()
	month := months[now.Month()-1]
	day := now.Day()

	// Simplified Roman numeral day
	var romanDay string
	switch {
	case day == 1:
		romanDay = "Kalendis"
	case day <= 4:
		romanDay = "IV"
	case day <= 9:
		romanDay = "IX"
	case day <= 14:
		romanDay = "XIV"
	case day <= 19:
		romanDay = "XIX"
	case day <= 24:
		romanDay = "XXIV"
	case day <= 29:
		romanDay = "XXIX"
	default:
		romanDay = "XXX"
	}

	return romanDay + " " + month
}

